import React from 'react';
import { StyleSheet,SafeAreaView,FlatList,Text,TouchableOpacity,View,Dimensions,StatusBar,Image } from 'react-native';
import { SearchBar } from 'react-native-elements';
import { useState }from "react";

const { width } = Dimensions.get('window');

const DATA = [
    {
      id: '1',
      url: "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQex0BrcitYx7Yrh-TwxTsbMN6gpXwYFtZUbw&usqp=CAU",
      title: '陽茶 Sun Tea',
      location: '2/F 203',
      info: 'A tea for good day'
    },
    {
        id: '2',
        url: "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQex0BrcitYx7Yrh-TwxTsbMN6gpXwYFtZUbw&usqp=CAU",
        title: 'Sun Tea',
        location: '2/F 203',
        info: 'A tea for good day'
    },
    {
        id: '3',
        url: "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQex0BrcitYx7Yrh-TwxTsbMN6gpXwYFtZUbw&usqp=CAU",
        title: 'Sun Tea',
        location: '2/F 203',
        info: 'A tea for good day'
    },
    {
        id: '4',
        url: "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQex0BrcitYx7Yrh-TwxTsbMN6gpXwYFtZUbw&usqp=CAU",
        title: 'Sun Tea',
        location: '2/F 203',
        info: 'A tea for good day'
    },
    {
        id: '5',
        url: "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQex0BrcitYx7Yrh-TwxTsbMN6gpXwYFtZUbw&usqp=CAU",
        title: 'Sun Tea',
        location: '2/F 203',
        info: 'A tea for good day'
    },

]

const FlatListItem = (props) => {
    return(
    <View style={{flex:1,flexDirection:'row',backgroundColor:'white',margin:10,borderRadius:10,shadowColor:"#000",shadowOffset:{width:0, height:3},shadowOpacity:0.3,shadowRadius:4.65,elevation:7}}>
        <Image source={{uri: props.item.url}} style={{width:120, height: 120,borderBottomLeftRadius:10,borderTopLeftRadius:10,borderRightWidth:1,borderColor:"#9b9b9b"}}></Image>
        <View style={{flex:1,flexDirection:'column'}}>
        <Text style={styles.title}>{props.item.title}</Text>
        <Text style={styles.location}>{props.item.location}</Text>
        <Text style={styles.info}>{props.item.info}</Text>
        </View>
    </View>
    )
}

const ShopList = (props) => {

    return(
        <SafeAreaView style={styles.container}>
        <FlatList
        data={DATA}
        renderItem={({item, index})=>{
            return (<FlatListItem item={item} index={index}></FlatListItem>)
        }}
        keyExtractor={item => item.id}
        ></FlatList>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      marginTop: StatusBar.currentHeight || 0,
    },
    item: {
        backgroundColor: '#f9c2ff',
        padding: 20,
        marginVertical: 8,
        marginHorizontal: 16,
      },
    title: {
        color: 'black',
        fontWeight: 'bold',
        padding: 10,
        fontSize: 20,
    },
    location: {
        color: 'black',
        padding: 5,
        fontSize: 16
    },
    info: {
        color: 'black',
        padding: 5,
        fontSize: 12,
    },
});

export default ShopList;
