import React from 'react';
import { StyleSheet,SafeAreaView,FlatList,Text,TouchableOpacity,View,Dimensions,StatusBar,Image } from 'react-native';
import { SearchBar } from 'react-native-elements';
import { useState }from "react";

const { width } = Dimensions.get('window');

const DATA = [
    {
      id: '1',
      url: "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQex0BrcitYx7Yrh-TwxTsbMN6gpXwYFtZUbw&usqp=CAU",
      title: 'Sun Tea',
      location: '2/F 203',
      info: 'A tea for good day'
    }
]

const FlatListItem = (props) => {
    <View style={{flex:1,flexDirection:'row',backgroundColor:'tomato'}}>
        <Image source={{uri: props.item.url}}></Image>
        <View style={{flex:1,flexDirection:'column'}}>
        <Text>{props.item.title}</Text>
        <Text>{props.item.location}</Text>
        <Text>{props.info.location}</Text>
        </View>
    </View>
}

const ShopList = (props) => {

    return(
        <SafeAreaView style={styles.container}>
        <FlatList
        data={DATA}
        renderItem={({item, index})=>{
            console.log("Item = "+JSON.stringfy(item)+", index = "+index);
        }}
        keyExtractor={item => item.id}
        />
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      marginTop: StatusBar.currentHeight || 0,
    },
    item: {
        backgroundColor: '#f9c2ff',
        padding: 20,
        marginVertical: 8,
        marginHorizontal: 16,
      },
      title: {
        fontSize: 32,
      },
});

export default ShopList;
