import React from 'react';
import { StyleSheet,ScrollView,Text,TouchableOpacity,View,Dimensions,ImageBackground } from 'react-native';
import {Container, Header, Left,Body, Right, Icon} from 'native-base';
import Carousel from 'react-native-snap-carousel'; 
import MySearchBar from '../components/MySearchBar';
import ShopList from '../components/ShopList'
import Mall from './Mall'
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

let { width } = Dimensions.get('window');
if(width>500){
  width=500
}
const { height } = Dimensions.get('window');

const Stack = createStackNavigator();

class Index extends React.Component {

    state = {
        search: '',
    };

    updateSearch = (search) => {
        this.setState({ search });
    };

    render(){

        const startSearching = (search) => {
            navigation.navigate('ShopList', {
              search: search,
            });
          }

        return (
        <View style={styles.container}>
        <Header transparent>
            <Left style={{flex:1}}>
            <Icon name="menu" onPress={() => this.props.navigation.openDrawer()}/>
            </Left>
            <Body style={{flex:10}}>
            <MySearchBar onSubmitEditing={(search)=>{startSearching(search)}}/>
            </Body>
        </Header>
        <Stack.Navigator initialRouteName="Mall">
            <Stack.Screen name="Mall" component={Mall} />
            <Stack.Screen name="ShopList" component={ShopList} />
        </Stack.Navigator>
        </View>
  )}
}

const styles = {
    container: {
      flex: 1,
      backgroundColor: '#D5E2E2'
    }
}

export default Index;
